function _Prediction:ValidRequest(TypeOfPrediction)
    if os.clock() - self.LastRequest < self:TimeRequest(TypeOfPrediction) then
        return false
    else
        self.LastRequest = os.clock()
        return true
    end
end

function _Prediction:AccuracyToHitChance(TypeOfPrediction, Accuracy)
    if TypeOfPrediction == "VPrediction" then
        if Accuracy >= 90 then
            return 3
        elseif Accuracy >= 60 then
            return 2
        elseif Accuracy >= 30 then
            return 1
        else
            return 0
        end
    elseif TypeOfPrediction == "Prodiction" then
        if Accuracy >= 90 then
            return 3
        elseif Accuracy >= 60 then
            return 2
        elseif Accuracy >= 30 then
            return 1
        else
            return 0
        end
    elseif TypeOfPrediction == "DivinePred" then
        if Accuracy >= 90 then
            return math.max((Accuracy / 2)/100 + 1 - 0, 1)
        elseif Accuracy >= 80 then
            return math.max((Accuracy / 2)/100 + 1 - 0.05, 1)
        elseif Accuracy >= 50 then
            return math.max((Accuracy / 2)/100 + 1 - 0.1, 1)
        elseif Accuracy >= 20 then
            return math.max((Accuracy / 2)/100 + 1 - 0.05, 1)
        else
            return math.max((Accuracy / 2)/100 + 1 - 0, 1)
        end
    elseif TypeOfPrediction == "HPrediction" then
        return (Accuracy/100) * 3
    end
end

function _Prediction:TimeRequest(TypeOfPrediction)
    if TypeOfPrediction == "VPrediction" then
        return 0
    elseif TypeOfPrediction == "Prodiction" then
        return 0
    elseif TypeOfPrediction == "DivinePred" then
        return 0.15
    elseif TypeOfPrediction == "HPrediction" then
        return 0
    end
end

function _Prediction:GetPrediction(target, sp)
    assert(sp and type(sp) == "table", "Prediction:GetPrediction() Table is invalid!")
    local TypeOfPrediction = sp.TypeOfPrediction ~= nil and sp.TypeOfPrediction or "VPrediction"
    if IsValidTarget(target) and self:ValidRequest(TypeOfPrediction) then
        local delay = sp.Delay ~= nil and sp.Delay or 0
        local width = sp.Width ~= nil and sp.Width or 1
        local range = sp.Range ~= nil and sp.Range or math.huge
        local speed = sp.Speed ~= nil and sp.Speed or math.huge
        local skillshotType = sp.Type ~= nil and sp.Type or SPELL_TYPE.CIRCULAR
        local collision = sp.Collision ~= nil and sp.Collision or false
        local aoe = sp.Aoe ~= nil and sp.Aoe or false
        local accuracy = sp.Accuracy ~= nil and sp.Accuracy or 60
        local source = sp.Source ~= nil and sp.Source or myHero
        -- VPrediction
        if TypeOfPrediction == "VPrediction" or not target.type:lower():find("hero") then
            TypeOfPrediction = "VPrediction"
            if skillshotType == SPELL_TYPE.LINEAR then
                if aoe then
                    local castpos, hitchance, count, position = self.VP:GetLineAOECastPosition(target, delay, width, range, speed, source)
                    if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return castpos, true, count, position
                    else
                        return castpos, false, count, position
                    end
                else
                    local castpos, hitchance, position = self.VP:GetLineCastPosition(target, delay, width, range, speed, source, collision)
                    if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return castpos, true, position
                    else
                        return castpos, false, position
                    end
                end
            elseif skillshotType == SPELL_TYPE.CIRCULAR then
                if aoe then
                    local castpos, hitchance, count, position = self.VP:GetCircularAOECastPosition(target, delay, width, range, speed, source)
                    if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return castpos, true, count, position
                    else
                        return castpos, false, count, position
                    end
                else
                    local castpos, hitchance, position = self.VP:GetCircularCastPosition(target, delay, width, range, speed, source, collision)
                    if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return castpos, true, position
                    else
                        return castpos, false, position
                    end
                end
             elseif skillshotType == SPELL_TYPE.CONE then
                if aoe then
                    local castpos, hitchance, count, position = self.VP:GetConeAOECastPosition(target, delay, width, range, speed, source)
                    if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return castpos, true, count, position
                    else
                        return castpos, false, count, position
                    end
                else
                    local castpos, hitchance, position = self.VP:GetLineCastPosition(target, delay, width, range, speed, source, collision)
                    if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return castpos, true, position
                    else
                        return castpos, false, position
                    end
                end
            end
        -- Prodiction
        elseif TypeOfPrediction == "Prodiction" then
            --local aoe = false -- temp fix for prodiction
            if aoe then
                if skillshotType == SPELL_TYPE.LINEAR then
                    local pos, info, objects = Prodiction.GetLineAOEPrediction(target, range, speed, delay, width, source)
                    local hitChance = collision and info.mCollision() and -1 or info.hitchance
                    if hitChance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return pos, true, #objects, pos
                    else
                        return pos, false, #objects, pos
                    end
                elseif skillshotType == SPELL_TYPE.CIRCULAR then
                    local pos, info, objects = Prodiction.GetCircularAOEPrediction(target, range, speed, delay, width, source)
                    local hitChance = collision and info.mCollision() and -1 or info.hitchance
                    if hitChance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return pos, true, #objects, pos
                    else
                        return pos, false, #objects, pos
                    end
                    return pos, hitChance, #objects
                 elseif skillshotType == SPELL_TYPE.CONE then
                    local pos, info, objects = Prodiction.GetConeAOEPrediction(target, range, speed, delay, width, source)
                    local hitChance = collision and info.mCollision() and -1 or info.hitchance
                    if hitChance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                        return pos, true, #objects, pos
                    else
                        return pos, false, #objects, pos
                    end
                end
            else
                local pos, info = Prodiction.GetPrediction(target, range, speed, delay, width, source)
                local hitChance = collision and info.mCollision() and -1 or info.hitchance
                if hitChance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                    return pos, true, info.pos
                else
                    return pos, false, info.pos
                end
            end
        elseif TypeOfPrediction == "DivinePred" then
            local mySkill = nil
            local col = collision and 0 or math.huge
            if skillshotType == SPELL_TYPE.LINEAR then
                mySkill = LineSS(speed, range, width, delay * 1000, col)
            elseif skillshotType == SPELL_TYPE.CIRCULAR then
                mySkill = CircleSS(speed, range, width, delay * 1000, col)
            elseif skillshotType == SPELL_TYPE.CONE then
                mySkill = ConeSS(speed, range, width, delay * 1000, col)
            end
            local state, pos, perc = self.DP:predict(DPTarget(target), mySkill, self:AccuracyToHitChance(TypeOfPrediction, accuracy), Vector(source))
            if state == SkillShot.STATUS.SUCCESS_HIT then
                return pos, true, pos
            else
                return pos, false, pos
            end
        elseif TypeOfPrediction == "HPrediction" then
            local tipo = "PromptCircle"
            local tab = {}
            if skillshotType == SPELL_TYPE.LINEAR or skillshotType == SPELL_TYPE.CONE then
                width = 2 * width
                if speed ~= math.huge then 
                    tipo = "DelayLine"
                    tab.speed = speed
                else
                    tipo = "PromptLine"
                end
                if collision then
                    tab.collisionM = collision
                    tab.collisionH = collision
                end
                if skillshotType == SPELL_TYPE.CONE then
                    width = width * math.min(GetDistance(target, source) / range, 1)
                end
                tab.width = width
                if width <= 120 then tab.IsVeryLowAccuracy = true end
            elseif skillshotType == SPELL_TYPE.CIRCULAR then
                tab.radius = width
                if speed ~= math.huge then 
                    tipo = "DelayCircle"
                    tab.speed = speed
                else
                    tipo = "PromptCircle"
                end
                if width <= 90 then tab.IsVeryLowAccuracy = true end
            end
            tab.range = range
            tab.delay = delay
            tab.type = tipo
            if aoe then
                local pos, hitchance, noh = self.HP:GetPredict(HPSkillshot(tab), target, source, aoe)--, range, width
                if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                    return pos, true, noh, pos
                else
                    return pos, false, noh, pos
                end
            else
                local pos, hitchance = self.HP:GetPredict(HPSkillshot(tab), target, source)--, range, width
                if hitchance >= self:AccuracyToHitChance(TypeOfPrediction, accuracy) then
                    return pos, true, pos
                else
                    return pos, false, pos
                end
            end
        end
    end
    return Vector(target), false, Vector(target)
end

function _Prediction:GetPredictedPos(target, tab)
    local delay = tab.Delay ~= nil and tab.Delay or 0
    local speed = tab.Speed or nil
    local from = tab.Source or nil
    local collision = tab.Collision or nil
    local TypeOfPrediction = tab.TypeOfPrediction ~= nil and tab.TypeOfPrediction or "VPrediction"
    local accuracy = tab.Accuracy ~= nil and tab.Accuracy or 60
    local CastPosition, HitChance, Position = self.VP:GetPredictedPos(target, delay, speed, from, collision)
    local WillHit = false
    if HitChance > 0 then
        WillHit = true
    else
        WillHit = false
    end
    return CastPosition, WillHit, Position
end


--CLASS: _OrbwalkManager
function _OrbwalkManager:__init()
    self.OrbLoaded = ""
    self.OrbwalkList = {}
    if _G.Reborn_Loaded then
        table.insert(self.OrbwalkList, "AutoCarry")
    end
    if FileExist(LIB_PATH .. "SxOrbWalk.lua") then
        table.insert(self.OrbwalkList, "SxOrbWalk")
    end
    if FileExist(LIB_PATH .. "SOW.lua") then
        table.insert(self.OrbwalkList, "SOW")
    end

    if FileExist(LIB_PATH .. "Big Fat Orbwalker.lua") then
        table.insert(self.OrbwalkList, "Big Fat Walk")
    end
    self.Attack = true
    self.Move = true
    self.KeysMenu = nil
    self.GotReset = false
    self.DataUpdated = false
    self.BaseWindUpTime = 3
    self.BaseAnimationTime = 0.665
    self.Mode = ORBWALK_MODE.NONE
    self.KeyMan = _KeyManager()
    self.AfterAttackCallbacks = {}
    self.LastAnimationName = ""
    self.AA = {LastTime = 0, LastTarget = nil, IsAttacking = false, Object = nil}
    
    self.EnemyMinions = minionManager(MINION_ENEMY, 1000, myHero, MINION_SORT_HEALTH_ASC)
    self.JungleMinions = minionManager(MINION_JUNGLE, 1000, myHero, MINION_SORT_MAXHEALTH_DEC)

    if _G.OrbwalkManagerMenu == nil then
        _G.OrbwalkManagerMenu = scriptConfig("Orbwalk Manager", "OrbwalkManager".."24052015"..myHero.charName)
    end
    if #self.OrbwalkList > 0 then
        _G.OrbwalkManagerMenu:addParam("OrbwalkerSelected", "Orbwalker Selection", SCRIPT_PARAM_LIST, 1, self.OrbwalkList)
        if #self.OrbwalkList > 1 then
            _G.OrbwalkManagerMenu:addParam("info", "Requires 2x F9 when changing selection", SCRIPT_PARAM_INFO, "")
        end
    end
    DelayAction(function() self:OrbLoad() end, 10)

    AddCreateObjCallback(
        function(obj)
            if self.AA.Object == nil and obj.name:lower() == "missile" and not self:CanMove() and GetDistanceSqr(Vector(myHero.x, 0, myHero.z), Vector(obj.x, 0, obj.z)) < 150 * 150 then
                self.AA.Object = obj
            end
        end
    )

    AddDeleteObjCallback(
        function(obj)
            if obj and self.AA.Object ~= nil and obj.name == self.AA.Object.name and GetDistanceSqr(obj, self.AA.Object) < 100 * 100 then
                self:ResetMove()
            end
        end
    )
    AddAnimationCallback(
        function(unit, animation)
            if unit.isMe then
                if self:IsAttacking() then
                    if not animation:lower():find("attack") then
                        self:ResetMove()
                    end
                end
                self.LastAnimationName = animation
            end
        end
    )
    AddProcessSpellCallback(function(unit, spell) self:OnProcessSpell(unit, spell) end)
    AddTickCallback(
        function()
            self.Mode = self.KeyMan:ModePressed()
            if not self:IsAttacking() then
                if self.AA.IsAttacking then
                    self:TriggerAfterAttackCallback(spell)
                    self.AA.IsAttacking = false
                    if self.GotReset then self.GotReset = false end
                end
            end
        end
    )
end

function _OrbwalkManager:GetOrbwalkSelected()
    local int = _G.OrbwalkManagerMenu ~= nil and _G.OrbwalkManagerMenu.OrbwalkerSelected or 1
    return #self.OrbwalkList > 0 and tostring(self.OrbwalkList[int]) or nil
end

